import { useState } from 'react'
import '../App.css'

const URI = process.env.REACT_APP_URI;

const state = {
    button: 1
};

export const CrearNota = () => {

    const [titulo, setTitulo] = useState('')
    const [descripcion, setDescripcion] = useState('')

    const handleSutmit = async (e) => {
        if (state.button === 1) {
            console.log("Boton de cerrar oprimido!")
        }
        if (status.button === 2) {
            e.preventDefault();
            const res = await fetch(`${URI}crearNotas`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    titulo,
                    descripcion
                })
            })
            const data = await res.json();
            console.log(data);
        }

    }

    return (
        <form className="login-form" onSubmit={handleSutmit}>
            <nav className="navbar navbar-light bg-noteapp mb-3">
                <div className="container-fluid">
                    <span className="navbar-brand mb-0 h1">Crear Nota</span>
                </div>
            </nav>
            <div className="mb-3">
                <input
                    className="form-control"
                    id="exampleFormControlInput1"
                    placeholder="Título"
                    onChange={e => setTitulo(e.target.value)}
                    value={titulo}
                />
            </div>
            <div className="mb-3">
                <textarea
                    className="form-control"
                    id="exampleFormControlTextarea1"
                    rows={5}
                    placeholder="Descripción"
                    onChange={e => setDescripcion(e.target.value)}
                    value={descripcion}
                />
            </div>
            <div className="text-right">
                <button
                    className="btn btn-danger mx-2"
                    type="submit"
                    name="cancelar"
                    value="cancelar"
                    onClick={() => (state.button = 1)}>
                    Cancelar
                </button>
                <button
                    className="btn btn-info"
                    type="submit"
                    name="crear"
                    value="crear"
                    onClick={() => (state.button = 2)}>
                    Crear Nota
                </button>
            </div>
        </form>
    )

}
