import { useState } from 'react';
import '../App.css';

const URI = process.env.REACT_APP_URI;

export const Principal = () => {

    const [titulo, setTitulo] = useState('')
    const [descripcion, setDescripcion] = useState('')

    const handleSutmit = async (e) => {

        try {
            e.preventDefault();
            const res = await fetch(`${URI}crearNotas`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    titulo,
                    descripcion
                })
            })
            const data = await res.json();
            console.log(data);
            alert("Nueva nota creada")
        } catch (error) {
            alert("Ha ocurrido un error")
        }

    }

    return (
        <div>
            <nav className="navbar navbar-light bg-noteapp">
                <div className="container-fluid">
                    <span className="navbar-brand mb-0 h1"><strong>Note app</strong></span>
                </div>
            </nav>

            <div className="container">
                <div className="row">
                    <div className="col-4 pt-4">
                        <div >
                            <div>
                                <form className="crear-nota-form">
                                    <nav className="navbar navbar-light bg-noteapp mb-3 ">
                                        <div className="container-fluid">
                                            <span className="navbar-brand mb-0 h1"><strong>Crear Nota</strong></span>
                                        </div>
                                    </nav>
                                    <div className="mb-3">
                                        <input
                                            className="form-control"
                                            id="exampleFormControlInput1"
                                            placeholder="Título"
                                            onChange={e => setTitulo(e.target.value)}
                                            value={titulo}
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <textarea
                                            className="form-control"
                                            id="exampleFormControlTextarea1"
                                            rows={5}
                                            placeholder="Descripción"
                                            onChange={e => setDescripcion(e.target.value)}
                                            value={descripcion}
                                        />
                                    </div>
                                    <div className="text-right">

                                        <button
                                            className="btn btn-danger mx-2"
                                            type="submit"
                                            name="cancelar"
                                            value="cancelar"
                                            /*onClick={}*/>
                                            Cancelar
                                        </button>
                                        <button
                                            className="btn btn-info"
                                            type="submit"
                                            name="crear"
                                            value="crear"
                                            onClick={handleSutmit}>
                                            Crear Nota
                                        </button>
                                    </div>
                                </form>

                            </div>
                        </div>
                    </div>
                    <div className="col-8 pt-4">
                        <h1>Contenido con las notas</h1>
                    </div>
                </div>
            </div>
        </div>
    )
}
