import { useState } from 'react'
import '../App.css'

export const CrearNota = () => {

    const [titulo, setTitulo] = useState('')
    const [descripcion, setDescripcion] = useState('')

    const handleSutmit = (e) => {
        //e.preventDefault();
        console.log("Enviando");
    }

    return (
        <form className="card card-body" onSubmit={handleSutmit}>
            <nav className="navbar navbar-light bg-noteapp mb-3">
                <div className="container-fluid">
                    <span className="navbar-brand mb-0 h1">Crear Nota</span>
                </div>
            </nav>
            <div className="mb-3">
                <input
                    className="form-control"
                    id="exampleFormControlInput1"
                    placeholder="Título"
                    onChange={e => setTitulo(e.target.value)}
                    value={titulo}
                />
            </div>
            <div className="mb-3">
                <textarea
                    className="form-control"
                    id="exampleFormControlTextarea1"
                    rows={5}
                    placeholder="Descripción"
                    onChange={e => setDescripcion(e.target.value)} 
                    value={descripcion}
                    />
            </div>
            <div className="text-right">
                <button type="button" className="btn btn-danger mx-2">Cancelar</button>
                <button type="button" className="btn btn-info">Crear Nota</button>
            </div>
        </form>
    )

}
